var app=angular.module("Todo",["ngAnimate"]);app.controller("TodoController",function(a,b){b.get("/javascripts/data.json").success(function(b){a.items=b,a.loaded=!0}),a.addItem=function(){a.items.push(a.newToDo),a.newToDo={priority:"low"}},a.removeItems=function(){a.items=a.items.filter(function(a){return!a.done})},a.completeItems=function(){for(a.toggle=!a.toggle,i=0;i<a.items.length;i++)a.items[i].done=a.toggle}});